class Qwen2_5_VLMLP(nn.Module):
    """
    Qwen2.5-VL å¤šå±‚æ„ŸçŸ¥æœºï¼ˆMLPï¼‰æ¨¡å—ã€‚
    
    å®žçŽ°æ ‡å‡†çš„ SwiGLU æ¿€æ´»å‡½æ•°çš„å‰é¦ˆç½‘ç»œï¼ŒåŒ…å«é—¨æŽ§æœºåˆ¶ã€‚
    è¿™æ˜¯ Transformer å—ä¸­çš„å‰é¦ˆç½‘ç»œç»„ä»¶ã€‚
    
    Args:
        config: æ¨¡åž‹é…ç½®å¯¹è±¡
        bias (bool): æ˜¯å¦åœ¨çº¿æ€§å±‚ä¸­ä½¿ç”¨åç½®ï¼Œé»˜è®¤ False
    """
    def __init__(self, config, bias: bool = False):
        super().__init__()
        self.hidden_size = config.hidden_size
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=bias)
        self.up_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=bias)
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=bias)
        self.act_fn = ACT2FN[config.hidden_act]

    def forward(self, hidden_state):
        return self.down_proj(self.act_fn(self.gate_proj(hidden_state)) * self.up_proj(hidden_state))


class Qwen2_5_VisionPatchEmbed(nn.Module):
    """
    Qwen2.5-VL è§†è§‰å—åµŒå…¥æ¨¡å—ã€‚
    
    å°†è¾“å…¥çš„å›¾åƒæˆ–è§†é¢‘å—è½¬æ¢ä¸ºåµŒå…¥å‘é‡ã€‚ä½¿ç”¨ 3D å·ç§¯å¤„ç†æ—¶ç©ºä¿¡æ¯ï¼Œ
    æ”¯æŒå›¾åƒï¼ˆæ—¶é—´ç»´åº¦ä¸º1ï¼‰å’Œè§†é¢‘ï¼ˆæ—¶é—´ç»´åº¦>1ï¼‰è¾“å…¥ã€‚
    
    Args:
        patch_size (int): ç©ºé—´å—å¤§å°ï¼Œé»˜è®¤ 14
        temporal_patch_size (int): æ—¶é—´å—å¤§å°ï¼Œé»˜è®¤ 2
        in_channels (int): è¾“å…¥é€šé“æ•°ï¼Œé»˜è®¤ 3ï¼ˆRGBï¼‰
        embed_dim (int): åµŒå…¥ç»´åº¦ï¼Œé»˜è®¤ 1152
    """
    def __init__(
        self,
        patch_size: int = 14,
        temporal_patch_size: int = 2,
        in_channels: int = 3,
        embed_dim: int = 1152,
    ) -> None:
        super().__init__()
        self.patch_size = patch_size
        self.temporal_patch_size = temporal_patch_size
        self.in_channels = in_channels
        self.embed_dim = embed_dim

        kernel_size = [temporal_patch_size, patch_size, patch_size]
        self.proj = nn.Conv3d(in_channels, embed_dim, kernel_size=kernel_size, stride=kernel_size, bias=False)

    def forward(self, hidden_states: torch.Tensor) -> torch.Tensor:
        target_dtype = self.proj.weight.dtype
        hidden_states = hidden_states.view(
            -1, self.in_channels, self.temporal_patch_size, self.patch_size, self.patch_size
        )
        hidden_states = self.proj(hidden_states.to(dtype=target_dtype)).view(-1, self.embed_dim)
        return hidden_states


class Qwen2_5_VisionRotaryEmbedding(nn.Module):
    """
    Qwen2.5-VL è§†è§‰æ—‹è½¬ä½ç½®ç¼–ç æ¨¡å—ã€‚
    
    ä¸ºè§†è§‰ Transformer ç”Ÿæˆæ—‹è½¬ä½ç½®ç¼–ç ï¼ˆRoPEï¼‰ï¼Œç”¨äºŽç¼–ç ä½ç½®ä¿¡æ¯ã€‚
    
    Args:
        dim (int): åµŒå…¥ç»´åº¦
        theta (float): RoPE çš„åŸºç¡€é¢‘çŽ‡ï¼Œé»˜è®¤ 10000.0
    """
    inv_freq: torch.Tensor  # fix linting for `register_buffer`

    def __init__(self, dim: int, theta: float = 10000.0) -> None:
        super().__init__()
        inv_freq = 1.0 / (theta ** (torch.arange(0, dim, 2, dtype=torch.float) / dim))
        self.register_buffer("inv_freq", inv_freq, persistent=False)

    def forward(self, seqlen: int) -> torch.Tensor:
        seq = torch.arange(seqlen, device=self.inv_freq.device, dtype=self.inv_freq.dtype)
        freqs = torch.outer(seq, self.inv_freq)
        return freqs


class Qwen2RMSNorm(nn.Module):
    """
    Qwen2 RMS å½’ä¸€åŒ–å±‚ã€‚
    
    å®žçŽ° Root Mean Square å½’ä¸€åŒ–ï¼Œç­‰ä»·äºŽ T5LayerNormã€‚
    ç”¨äºŽç¨³å®šè®­ç»ƒå’Œæé«˜æ¨¡åž‹æ€§èƒ½ã€‚
    
    Args:
        hidden_size (int): éšè—å±‚ç»´åº¦
        eps (float): æ•°å€¼ç¨³å®šæ€§çš„å°å¸¸æ•°ï¼Œé»˜è®¤ 1e-6
    """
    def __init__(self, hidden_size, eps=1e-6):
        super().__init__()
        self.weight = nn.Parameter(torch.ones(hidden_size))
        self.variance_epsilon = eps

    def forward(self, hidden_states):
        input_dtype = hidden_states.dtype
        hidden_states = hidden_states.to(torch.float32)
        variance = hidden_states.pow(2).mean(-1, keepdim=True)
        hidden_states = hidden_states * torch.rsqrt(variance + self.variance_epsilon)
        return self.weight * hidden_states.to(input_dtype)

    def extra_repr(self):
        return f"{tuple(self.weight.shape)}, eps={self.variance_epsilon}"


class Qwen2_5_VLPatchMerger(nn.Module):
    """
    Qwen2.5-VL å—åˆå¹¶å™¨ã€‚
    
    å°†å¤šä¸ªç›¸é‚»çš„è§†è§‰å—åˆå¹¶ä¸ºå•ä¸ªå—ï¼Œä»¥å‡å°‘åºåˆ—é•¿åº¦å¹¶æé«˜è®¡ç®—æ•ˆçŽ‡ã€‚
    ä½¿ç”¨ MLP ç½‘ç»œè¿›è¡Œç‰¹å¾å˜æ¢å’Œç»´åº¦è°ƒæ•´ã€‚
    
    Args:
        dim (int): è¾“å‡ºç»´åº¦
        context_dim (int): è¾“å…¥ä¸Šä¸‹æ–‡ç»´åº¦
        spatial_merge_size (int): ç©ºé—´åˆå¹¶å¤§å°ï¼Œé»˜è®¤ 2ï¼ˆå³ 2x2 åˆå¹¶ï¼‰
    """
    def __init__(self, dim: int, context_dim: int, spatial_merge_size: int = 2) -> None:
        super().__init__()
        self.hidden_size = context_dim * (spatial_merge_size**2)
        self.ln_q = Qwen2RMSNorm(context_dim, eps=1e-6)
        self.mlp = nn.Sequential(
            nn.Linear(self.hidden_size, self.hidden_size),
            nn.GELU(),
            nn.Linear(self.hidden_size, dim),
        )

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        x = self.mlp(self.ln_q(x).view(-1, self.hidden_size))
        return x


def rotate_half(x):
    """
    æ—‹è½¬è¾“å…¥å¼ é‡çš„ä¸€åŠéšè—ç»´åº¦ã€‚
    
    è¿™æ˜¯æ—‹è½¬ä½ç½®ç¼–ç ï¼ˆRoPEï¼‰çš„æ ¸å¿ƒæ“ä½œï¼Œå°†è¾“å…¥å¼ é‡çš„åŽåŠéƒ¨åˆ†å–è´Ÿå·å¹¶ä¸Žå‰åŠéƒ¨åˆ†äº¤æ¢ä½ç½®ã€‚
    ç”¨äºŽå®žçŽ°å¤æ•°ä¹˜æ³•çš„æ—‹è½¬æ“ä½œã€‚
    
    Args:
        x (torch.Tensor): è¾“å…¥å¼ é‡ï¼Œshape: (..., hidden_dim)
    
    Returns:
        torch.Tensor: æ—‹è½¬åŽçš„å¼ é‡ï¼Œshape ä¸Žè¾“å…¥ç›¸åŒ
    
    ç¤ºä¾‹:
        å¦‚æžœè¾“å…¥ x = [a, b, c, d]ï¼Œåˆ™è¾“å‡ºä¸º [-c, -d, a, b]
    """
    x1 = x[..., : x.shape[-1] // 2]  # å‰åŠéƒ¨åˆ†
    x2 = x[..., x.shape[-1] // 2 :]  # åŽåŠéƒ¨åˆ†
    return torch.cat((-x2, x1), dim=-1)  # æ‹¼æŽ¥ï¼š(-åŽåŠéƒ¨åˆ†, å‰åŠéƒ¨åˆ†)


def apply_rotary_pos_emb_vision(
    q: torch.Tensor, k: torch.Tensor, cos: torch.Tensor, sin: torch.Tensor
) -> tuple[torch.Tensor, torch.Tensor]:
    """
    ä¸ºè§†è§‰æ¨¡åž‹çš„æŸ¥è¯¢å’Œé”®å¼ é‡åº”ç”¨æ—‹è½¬ä½ç½®ç¼–ç ï¼ˆRoPEï¼‰ã€‚
    
    æ—‹è½¬ä½ç½®ç¼–ç é€šè¿‡å¤æ•°ä¹˜æ³•çš„æ–¹å¼å°†ä½ç½®ä¿¡æ¯ç¼–ç åˆ°æŸ¥è¯¢å’Œé”®å‘é‡ä¸­ï¼Œ
    ä½¿æ¨¡åž‹èƒ½å¤Ÿç†è§£åºåˆ—ä¸­å…ƒç´ çš„ç›¸å¯¹ä½ç½®å…³ç³»ã€‚å¯¹äºŽè§†è§‰æ¨¡åž‹ï¼Œè¿™æœ‰åŠ©äºŽ
    ç†è§£å›¾åƒå—ä¹‹é—´çš„ç©ºé—´å…³ç³»ã€‚
    
    Args:
        q (torch.Tensor): æŸ¥è¯¢å¼ é‡ï¼Œshape: (batch_size, num_heads, seq_len, head_dim)
        k (torch.Tensor): é”®å¼ é‡ï¼Œshape: (batch_size, num_heads, seq_len, head_dim)
        cos (torch.Tensor): ä½™å¼¦ä½ç½®ç¼–ç ï¼Œshape: (seq_len, head_dim)
        sin (torch.Tensor): æ­£å¼¦ä½ç½®ç¼–ç ï¼Œshape: (seq_len, head_dim)
    
    Returns:
        tuple[torch.Tensor, torch.Tensor]: åº”ç”¨ RoPE åŽçš„æŸ¥è¯¢å’Œé”®å¼ é‡
            - q_embed: ç¼–ç åŽçš„æŸ¥è¯¢å¼ é‡
            - k_embed: ç¼–ç åŽçš„é”®å¼ é‡
    
    æ³¨æ„:
        è®¡ç®—è¿‡ç¨‹ä¸­ä¼šè½¬æ¢ä¸º float32 ä»¥æé«˜æ•°å€¼ç¨³å®šæ€§ï¼Œæœ€åŽæ¢å¤åŽŸå§‹æ•°æ®ç±»åž‹ã€‚
    """
    # ä¿å­˜åŽŸå§‹æ•°æ®ç±»åž‹
    orig_q_dtype = q.dtype
    orig_k_dtype = k.dtype
    
    # è½¬æ¢ä¸º float32 ä»¥æé«˜æ•°å€¼ç¨³å®šæ€§
    q, k = q.float(), k.float()
    cos, sin = cos.unsqueeze(-2).float(), sin.unsqueeze(-2).float()  # å¢žåŠ  head ç»´åº¦
    
    # åº”ç”¨æ—‹è½¬ä½ç½®ç¼–ç ï¼šå¤æ•°ä¹˜æ³•çš„å®žæ•°å½¢å¼
    # q_embed = q * cos + rotate_half(q) * sin
    q_embed = (q * cos) + (rotate_half(q) * sin)
    k_embed = (k * cos) + (rotate_half(k) * sin)
    
    # æ¢å¤åŽŸå§‹æ•°æ®ç±»åž‹
    q_embed = q_embed.to(orig_q_dtype)
    k_embed = k_embed.to(orig_k_dtype)
    
    return q_embed, k_embed


def repeat_kv(hidden_states: torch.Tensor, n_rep: int) -> torch.Tensor:
    """
    é‡å¤é”®å€¼å¯¹å¼ é‡ä»¥åŒ¹é…æŸ¥è¯¢å¤´çš„æ•°é‡ã€‚
    
    åœ¨åˆ†ç»„æŸ¥è¯¢æ³¨æ„åŠ›ï¼ˆGQAï¼‰ä¸­ï¼Œé”®å€¼å¯¹å¤´çš„æ•°é‡é€šå¸¸å°‘äºŽæŸ¥è¯¢å¤´çš„æ•°é‡ã€‚
    æ­¤å‡½æ•°é€šè¿‡é‡å¤é”®å€¼å¯¹å¼ é‡æ¥åŒ¹é…æŸ¥è¯¢å¤´çš„æ•°é‡ï¼Œå®žçŽ°é«˜æ•ˆçš„æ³¨æ„åŠ›è®¡ç®—ã€‚
    
    ç­‰ä»·äºŽ torch.repeat_interleave(x, dim=1, repeats=n_rep)ã€‚
    
    Args:
        hidden_states (torch.Tensor): è¾“å…¥çš„é”®æˆ–å€¼å¼ é‡
            shape: (batch, num_key_value_heads, seqlen, head_dim)
        n_rep (int): é‡å¤æ¬¡æ•°ï¼Œé€šå¸¸ç­‰äºŽ num_attention_heads // num_key_value_heads
    
    Returns:
        torch.Tensor: é‡å¤åŽçš„å¼ é‡
            shape: (batch, num_attention_heads, seqlen, head_dim)
    
    ç¤ºä¾‹:
        å¦‚æžœ num_key_value_heads=2, num_attention_heads=8ï¼Œåˆ™ n_rep=4
        è¾“å…¥ shape: (batch, 2, seqlen, head_dim)
        è¾“å‡º shape: (batch, 8, seqlen, head_dim)
    """
    batch, num_key_value_heads, slen, head_dim = hidden_states.shape
    
    # å¦‚æžœä¸éœ€è¦é‡å¤ï¼Œç›´æŽ¥è¿”å›ž
    if n_rep == 1:
        return hidden_states
    
    # åœ¨ç¬¬3ç»´æ’å…¥æ–°ç»´åº¦å¹¶æ‰©å±•ï¼Œç„¶åŽé‡å¡‘ä¸ºç›®æ ‡å½¢çŠ¶
    # (batch, num_kv_heads, 1, slen, head_dim) -> (batch, num_kv_heads, n_rep, slen, head_dim)
    hidden_states = hidden_states[:, :, None, :, :].expand(batch, num_key_value_heads, n_rep, slen, head_dim)
    
    # é‡å¡‘ä¸ºæœ€ç»ˆå½¢çŠ¶: (batch, num_kv_heads * n_rep, slen, head_dim)
    return hidden_states.reshape(batch, num_key_value_heads * n_rep, slen, head_dim)


def eager_attention_forward(
    module: nn.Module,
    query: torch.Tensor,
    key: torch.Tensor,
    value: torch.Tensor,
    attention_mask: Optional[torch.Tensor],
    scaling: float,
    dropout: float = 0.0,
    **kwargs,
):
    """
    æ‰§è¡Œæ ‡å‡†çš„å¤šå¤´æ³¨æ„åŠ›å‰å‘ä¼ æ’­ï¼ˆeager æ¨¡å¼ï¼‰ã€‚
    
    å®žçŽ°æ ‡å‡†çš„ç¼©æ”¾ç‚¹ç§¯æ³¨æ„åŠ›æœºåˆ¶ï¼Œæ”¯æŒåˆ†ç»„æŸ¥è¯¢æ³¨æ„åŠ›ï¼ˆGQAï¼‰ã€‚
    ä½¿ç”¨ PyTorch çš„æ ‡å‡†æ“ä½œè¿›è¡Œè®¡ç®—ï¼Œç›¸å¯¹äºŽä¼˜åŒ–çš„å®žçŽ°ï¼ˆå¦‚ Flash Attentionï¼‰
    æ›´å®¹æ˜“è°ƒè¯•ä½†å¯èƒ½æ•ˆçŽ‡è¾ƒä½Žã€‚
    
    Args:
        module (nn.Module): æ³¨æ„åŠ›æ¨¡å—ï¼Œç”¨äºŽèŽ·å–é…ç½®ä¿¡æ¯
        query (torch.Tensor): æŸ¥è¯¢å¼ é‡ï¼Œshape: (batch, num_heads, seq_len, head_dim)
        key (torch.Tensor): é”®å¼ é‡ï¼Œshape: (batch, num_kv_heads, seq_len, head_dim)
        value (torch.Tensor): å€¼å¼ é‡ï¼Œshape: (batch, num_kv_heads, seq_len, head_dim)
        attention_mask (Optional[torch.Tensor]): æ³¨æ„åŠ›æŽ©ç ï¼Œç”¨äºŽå±è”½æŸäº›ä½ç½®
        scaling (float): ç¼©æ”¾å› å­ï¼Œé€šå¸¸ä¸º 1/sqrt(head_dim)
        dropout (float): dropout æ¦‚çŽ‡ï¼Œé»˜è®¤ 0.0
        **kwargs: å…¶ä»–å‚æ•°ï¼ˆæœªä½¿ç”¨ï¼‰
    
    Returns:
        tuple[torch.Tensor, torch.Tensor]: 
            - attn_output: æ³¨æ„åŠ›è¾“å‡ºï¼Œshape: (batch, seq_len, num_heads, head_dim)
            - attn_weights: æ³¨æ„åŠ›æƒé‡ï¼Œshape: (batch, num_heads, seq_len, seq_len)
    
    è®¡ç®—æµç¨‹:
        1. é‡å¤é”®å€¼å¯¹ä»¥åŒ¹é…æŸ¥è¯¢å¤´æ•°é‡ï¼ˆç”¨äºŽ GQAï¼‰
        2. è®¡ç®—æ³¨æ„åŠ›åˆ†æ•°ï¼šQ @ K^T * scaling
        3. åº”ç”¨æ³¨æ„åŠ›æŽ©ç 
        4. Softmax å½’ä¸€åŒ–
        5. åº”ç”¨ dropout
        6. è®¡ç®—æœ€ç»ˆè¾“å‡ºï¼šattention_weights @ V
    """
    # 1. é‡å¤é”®å€¼å¯¹å¼ é‡ä»¥åŒ¹é…æŸ¥è¯¢å¤´æ•°é‡ï¼ˆç”¨äºŽåˆ†ç»„æŸ¥è¯¢æ³¨æ„åŠ›ï¼‰
    key_states = repeat_kv(key, module.num_key_value_groups)
    value_states = repeat_kv(value, module.num_key_value_groups)

    # 2. è®¡ç®—æ³¨æ„åŠ›åˆ†æ•°ï¼šQ @ K^Tï¼Œå¹¶åº”ç”¨ç¼©æ”¾å› å­
    # attn_weights: (batch, num_heads, seq_len, seq_len)
    attn_weights = torch.matmul(query, key_states.transpose(2, 3)) * scaling
    
    # 3. åº”ç”¨æ³¨æ„åŠ›æŽ©ç ï¼ˆå¦‚æžœæä¾›ï¼‰
    if attention_mask is not None:
        # è£å‰ªæŽ©ç ä»¥åŒ¹é…é”®çš„åºåˆ—é•¿åº¦
        causal_mask = attention_mask[:, :, :, : key_states.shape[-2]]
        attn_weights = attn_weights + causal_mask  # åŠ æ³•æŽ©ç ï¼ˆè´Ÿæ— ç©·è¡¨ç¤ºå±è”½ï¼‰

    # 4. Softmax å½’ä¸€åŒ–ï¼ˆä½¿ç”¨ float32 æé«˜æ•°å€¼ç¨³å®šæ€§ï¼‰
    attn_weights = nn.functional.softmax(attn_weights, dim=-1, dtype=torch.float32).to(query.dtype)
    
    # 5. åº”ç”¨ dropout
    attn_weights = nn.functional.dropout(attn_weights, p=dropout, training=module.training)
    
    # 6. è®¡ç®—æœ€ç»ˆè¾“å‡ºï¼šattention_weights @ V
    # attn_output: (batch, num_heads, seq_len, head_dim)
    attn_output = torch.matmul(attn_weights, value_states)
    
    # 7. è½¬ç½®ä»¥åŒ¹é…æœŸæœ›çš„è¾“å‡ºæ ¼å¼ï¼š(batch, seq_len, num_heads, head_dim)
    attn_output = attn_output.transpose(1, 2).contiguous()

    return attn_output, attn_weights


class Qwen2_5_VLVisionAttention(nn.Module):
    """
    Qwen2.5-VL è§†è§‰æ³¨æ„åŠ›æ¨¡å—ã€‚
    
    å®žçŽ°å¤šå¤´è‡ªæ³¨æ„åŠ›æœºåˆ¶ï¼Œæ”¯æŒæ—‹è½¬ä½ç½®ç¼–ç å’Œå¤šç§æ³¨æ„åŠ›å®žçŽ°æ–¹å¼ã€‚
    å¯ä»¥å¤„ç†å˜é•¿åºåˆ—å¹¶æ”¯æŒ Flash Attention ç­‰ä¼˜åŒ–å®žçŽ°ã€‚
    
    Args:
        config (Qwen2_5_VLVisionConfig): è§†è§‰é…ç½®å¯¹è±¡
    """
    def __init__(self, config: Qwen2_5_VLVisionConfig) -> None:
        super().__init__()
        self.dim = config.hidden_size
        self.num_heads = config.num_heads
        self.head_dim = self.dim // self.num_heads
        self.num_key_value_groups = 1  # needed for eager attention
        self.qkv = nn.Linear(self.dim, self.dim * 3, bias=True)
        self.proj = nn.Linear(self.dim, self.dim)
        self.scaling = self.head_dim**-0.5
        self.config = config
        self.attention_dropout = 0.0
        self.is_causal = False

    def forward(
        self,
        hidden_states: torch.Tensor,
        cu_seqlens: torch.Tensor,
        rotary_pos_emb: Optional[torch.Tensor] = None,
        position_embeddings: Optional[tuple[torch.Tensor, torch.Tensor]] = None,
        **kwargs,
    ) -> torch.Tensor:
        seq_length = hidden_states.shape[0]
        query_states, key_states, value_states = (
            self.qkv(hidden_states).reshape(seq_length, 3, self.num_heads, -1).permute(1, 0, 2, 3).unbind(0)
        )
        if position_embeddings is None:
            logger.warning_once(
                "The attention layers in this model are transitioning from computing the RoPE embeddings internally "
                "through `rotary_pos_emb` (2D tensor of RoPE theta values), to using externally computed "
                "`position_embeddings` (Tuple of tensors, containing cos and sin). In v4.54 `rotary_pos_emb` will be "
                "removed and `position_embeddings` will be mandatory."
            )
            emb = torch.cat((rotary_pos_emb, rotary_pos_emb), dim=-1)
            cos = emb.cos()
            sin = emb.sin()
        else:
            cos, sin = position_embeddings
        query_states, key_states = apply_rotary_pos_emb_vision(query_states, key_states, cos, sin)

        query_states = query_states.transpose(0, 1).unsqueeze(0)
        key_states = key_states.transpose(0, 1).unsqueeze(0)
        value_states = value_states.transpose(0, 1).unsqueeze(0)

        attention_interface: Callable = eager_attention_forward
        if self.config._attn_implementation != "eager":
            attention_interface = ALL_ATTENTION_FUNCTIONS[self.config._attn_implementation]

        if self.config._attn_implementation == "flash_attention_2":
            # Flash Attention 2: Use cu_seqlens for variable length attention
            max_seqlen = (cu_seqlens[1:] - cu_seqlens[:-1]).max()
            attn_output, _ = attention_interface(
                self,
                query_states,
                key_states,
                value_states,
                attention_mask=None,
                scaling=self.scaling,
                dropout=0.0 if not self.training else self.attention_dropout,
                cu_seq_lens_q=cu_seqlens,
                cu_seq_lens_k=cu_seqlens,
                max_length_q=max_seqlen,
                max_length_k=max_seqlen,
                is_causal=False,
                **kwargs,
            )
        else:
            # Other implementations: Process each chunk separately
            lengths = cu_seqlens[1:] - cu_seqlens[:-1]
            splits = [
                torch.split(tensor, lengths.tolist(), dim=2) for tensor in (query_states, key_states, value_states)
            ]

            attn_outputs = [
                attention_interface(
                    self,
                    q,
                    k,
                    v,
                    attention_mask=None,
                    scaling=self.scaling,
                    dropout=0.0 if not self.training else self.attention_dropout,
                    is_causal=False,
                    **kwargs,
                )[0]
                for q, k, v in zip(*splits)
            ]
            attn_output = torch.cat(attn_outputs, dim=1)

        attn_output = attn_output.reshape(seq_length, -1).contiguous()
        attn_output = self.proj(attn_output)
        return attn_output


class Qwen2_5_VLVisionBlock(GradientCheckpointingLayer):
    """
    Qwen2.5-VL è§†è§‰ Transformer å—ã€‚
    
    åŒ…å«å¤šå¤´è‡ªæ³¨æ„åŠ›å’Œå‰é¦ˆç½‘ç»œï¼Œä½¿ç”¨æ®‹å·®è¿žæŽ¥å’Œ RMS å½’ä¸€åŒ–ã€‚
    æ”¯æŒæ¢¯åº¦æ£€æŸ¥ç‚¹ä»¥èŠ‚çœå†…å­˜ï¼Œå¯é…ç½®ä¸åŒçš„æ³¨æ„åŠ›å®žçŽ°æ–¹å¼ã€‚
    
    Args:
        config: è§†è§‰é…ç½®å¯¹è±¡
        attn_implementation (str): æ³¨æ„åŠ›å®žçŽ°æ–¹å¼ï¼Œé»˜è®¤ "sdpa"
    """
    def __init__(self, config, attn_implementation: str = "sdpa") -> None:
        super().__init__()
        self.norm1 = Qwen2RMSNorm(config.hidden_size, eps=1e-6)
        self.norm2 = Qwen2RMSNorm(config.hidden_size, eps=1e-6)
        self.attn = Qwen2_5_VLVisionAttention(config=config)
        self.mlp = Qwen2_5_VLMLP(config, bias=True)

    def forward(
        self,
        hidden_states: torch.Tensor,
        cu_seqlens: torch.Tensor,
        rotary_pos_emb: Optional[torch.Tensor] = None,
        position_embeddings: Optional[tuple[torch.Tensor, torch.Tensor]] = None,
        **kwargs,
    ) -> torch.Tensor:
        hidden_states = hidden_states + self.attn(
            self.norm1(hidden_states),
            cu_seqlens=cu_seqlens,
            rotary_pos_emb=rotary_pos_emb,
            position_embeddings=position_embeddings,
            **kwargs,
        )
        hidden_states = hidden_states + self.mlp(self.norm2(hidden_states))
        return hidden_states


